var vm=new Vue({el:'#app',data:{item:'',code:'',search:'',categoryName:'',categoryList:[],tips:[],array:[]},created:function(){axios.get('https://raw.githubusercontent.com/ChornyArtsiom/phd/gh-pages/data.json').then(b=>{this.tips=b.data})},computed:{filteredList:function(){return this.tips.filter(b=>{if(''!=this.search&&b.category==this.categoryName)return b.code.toLowerCase().includes(this.search.toLowerCase())})},setCategoryList:function(){return Array.from(this.tips).map(b=>b.category).filter((b,c,d)=>d.indexOf(b)===c)}},methods:{setCategory(b){this.categoryName=b},clearCategory(){this.categoryName=''},clearQuery(){this.$refs.search.focus(),this.search=''}},filters:{count(b){return 0==Object.keys(b).length?'\u041D\u0438\u0447\u0435\u0433\u043E \u043D\u0435 \u043D\u0430\u0439\u0434\u0435\u043D\u043E.':Object.keys(b).length}}});window.onscroll=function(){scrollFunction()};function scrollFunction(){document.getElementById('toTopBtn').style.display=100<document.body.scrollTop||100<document.documentElement.scrollTop?'block':'none'}function topFunction(){document.body.scrollTop=0,document.documentElement.scrollTop=0}